definition.script:
pipeline {
    agent any
    options {
        timeout(time: 1, unit: 'HOURS')
        disableConcurrentBuilds(abortPrevious: true)
    }
    triggers { cron('H H * * *') }
    stages {
        stage('Checkout') {
            steps {
                git (url: 'https://github.com/openrewrite/rewrite-spring.git', branch: 'main', credentialsId: 'myGitCreds')
            }
        }
        stage('Download CLI') {
            steps {
                sh "curl --request GET https://acme.com/moderne-cli > mod"
                sh "chmod 755 mod"
            }
        }
        stage('Publish') {
            steps {
                withCredentials([[$class: 'UsernamePasswordMultiBinding', credentialsId: 'artifactCreds', usernameVariable: 'ARTIFACTS_PUBLISH_CRED_USR', passwordVariable: 'ARTIFACTS_PUBLISH_CRED_PWD']]) {
                    sh './mod config artifacts https://artifactory.moderne.ninja/artifactory/moderne-ingest --user ${ARTIFACTS_PUBLISH_CRED_USR} --password ${ARTIFACTS_PUBLISH_CRED_PWD}'
                    sh './mod build .'
                    sh './mod publish .'
                }
            }
        }
    }
    post {
        always {
            cleanWs()
        }
    }
}
